import styled from "styled-components";

export const Container = styled.div`
    h1{
        text-align: center;
        margin: 4rem 0;
    }

    span{
        font-weight: bold;
        font-size: 120%;
    }

    a:hover{
        transform: scale(1.1);
        transition:all .3s;
    }
`;

export const MovieList = styled.ul`

`;
